import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import Spinner from '../../../Assests/Spinner';
import { ContactServices } from '../../../Services/ContactServices';

const ContactList = () => {

  const [query, setQuery] = useState({
    text: ""
  });

  const [state, setState] = useState({
    loading: true,
    contacts: [],
    filteredContacts: [],
    errorMessage: ''
  });

  useEffect(() => {
    const fetchContacts = async () => {
      try {
        setState({ ...state, loading: true });
        const response = await ContactServices.getAllContacts();

        if (response.httpStatusCode === 200) {
          // Update the state with the fetched data
          setState({
            ...state,
            loading: false,
            contacts: response.data,
            filteredContacts: response.data
          });
        } else {
          throw new Error('Error fetching contacts');
        }
      } catch (error) {
        setState({ ...state, loading: false, errorMessage: 'Data not found' });
      }
    };

    fetchContacts();
  }, []);

  // Delete Contact
  const clickDelete = async (contactId) => {
    try {
      await ContactServices.deleteContact(contactId);
      const response = await ContactServices.getAllContacts();
      if (response.httpStatusCode === 200) {
        setState({
          ...state,
          loading: false,
          contacts: response.data,
          filteredContacts: response.data
        });
      }
    } catch (error) {
      setState({ ...state, errorMessage: 'Failed to delete contact' });
    }
  };

  // Search Contact
  const searchContact = (event) => {
    setQuery({ ...query, text: event.target.value });

    const filteredContacts = state.contacts.filter((contact) =>
      contact.name.toLowerCase().includes(event.target.value.toLowerCase())
    );
    setState({ ...state, filteredContacts });
  };

  const { loading, filteredContacts } = state;

  return (
    <>
      <section className="contact-search">
        <div className="container p-3">
          <div className="grid">
            <div className="row">
              <div className="col">
                <p className="h3">
                  Contact Manager{' '}
                  <Link to={'/Contacts/add'} className="btn btn-primary ms-2">
                    <i className="fa fa-plus-circle me-2" />
                    Add
                  </Link>
                </p>
                <p className="fst-italic">
                  Lorem ipsum dolor sit amet consectetur adipisicing elit.
                </p>
              </div>
            </div>
          </div>
          <div className="row">
            <div className="col-md-6">
              <form className="row">
                <div className="col">
                  <div className="mb-2">
                    <input
                      type="text"
                      name="text"
                      onChange={searchContact}
                      value={query.text}
                      placeholder="Search name"
                      className="form-control"
                    />
                  </div>
                </div>
              </form>
            </div>
          </div>
        </div>
      </section>

      {loading ? (
        <Spinner />
      ) : (
        <section className="contact-list">
          <div className="container">
            <div className="row">
              {filteredContacts.length > 0 ? (
                filteredContacts.map((contact) => (
                  <div className="col-md-6 mb-4" key={contact.id}>
                    <div className="card d-flex justify-content-around">
                      <div className="card-body">
                        <div className="row align-items-center">
                          <div className="col-md-4">
                            <img src={contact.photo} alt="" className="contact-img" />
                          </div>
                          <div className="col-md-7">
                            <ul className="list-group">
                              <li className="list-group-item list-group-item-action">
                                Name: {contact.name || 'N/A'}
                              </li>
                              <li className="list-group-item list-group-item-action">
                                Mobile: {contact.mobile || 'N/A'}
                              </li>
                              <li className="list-group-item list-group-item-action">
                                Email: {contact.email || 'N/A'}
                              </li>
                            </ul>
                          </div>
                          <div className="col-md-1 d-flex flex-column align-items-center">
                            <Link
                              to={`/Contacts/view/${contact.id}`}
                              className="btn btn-warning my-1"
                            >
                              <i className="fa fa-eye" />
                            </Link>
                            <Link
                              to={`/Contacts/edit/${contact.id}`}
                              className="btn btn-primary my-1"
                            >
                              <i className="fa fa-pen" />
                            </Link>
                            <button
                              className="btn btn-danger my-1"
                              onClick={() => clickDelete(contact.id)}
                            >
                              <i className="fa fa-trash" />
                            </button>
                          </div>
                        </div>
                      </div>
                    </div>
                  </div>
                ))
              ) : (
                <p>No contacts found.</p>
              )}
            </div>
          </div>
        </section>
      )}
    </>
  );
};

export default ContactList;
