// export default EditContact
import React, { useState, useEffect } from 'react';
import { Link, useNavigate, useParams } from 'react-router-dom';
import { ContactServices } from '../../../Services/ContactServices';

const EditContact = () => {
  const navigate = useNavigate();
  const { contactId } = useParams();

  const [state, setState] = useState({
    loading: true,
    contact: {
      name: '',
      photo: '',
      mobile: '',
      email: '',
      title: '',
      company: '',
      groupId: ''
    },
    errorMessage: ''
  });

  useEffect(() => {
    const fetchContact = async () => {
      try {
        setState((prevState) => ({ ...prevState, loading: true }));
        
        // Fetch contact from API
        const response = await ContactServices.getContact(contactId);

        // Check if response and data structure are correct
        if (response.data && response.data.data ) {
          // Find the contact by its id
          const foundContact = response.data.data

          if (foundContact) {
            setState({ ...state, loading: false, contact: foundContact });
          } else {
            setState({ ...state, loading: false, errorMessage: 'Contact not found' });
          }
        } else {
          setState({ ...state, loading: false, errorMessage: 'Invalid data structure from server' });
        }
      } catch (error) {
        setState({ ...state, loading: false, errorMessage: 'Error fetching contact data' });
      }
    };

    fetchContact();
  }, [contactId]);

  const { loading, contact, errorMessage } = state;

  const updateHandle = (event) => {
    setState({
      ...state,
      contact: {
        ...state.contact,
        [event.target.name]: event.target.value
      }
    });
  };

  const submitHandle = async (event) => {
    event.preventDefault();

    try {
      setState({ ...state, loading: true });

      // Update contact using API
      const postData = await ContactServices.updateContact(contact, contactId);

      if (postData) {
        setState({ ...state, loading: false });
        navigate('/Contacts/list', { replace: true });
      } else {
        setState({ ...state, loading: false, errorMessage: 'Failed to update contact' });
      }
    } catch (error) {
      setState({ ...state, loading: false, errorMessage: 'Error updating contact' });
    }
  };

  return (
    <>
      <section className='edit-contact'>
        <div className='container p-3'>
          <div className='row'>
            <div className='col'>
              <p className='h3 text-primary'>Edit Contact</p>
              <p className='fst-italic'>
                Lorem ipsum dolor sit amet consectetur adipisicing elit. In natus ipsum doloribus rerum aperiam est quibusdam? Quam unde eligendi delectus veritatis molestias officiis omnis illum, soluta at. Deserunt, aliquid harum.
              </p>
            </div>
            <div className='row align-items-center'>
              <div className='col-md-4'>
                <form onSubmit={submitHandle}>
                  <div className='mb-2'>
                    <input
                      type='text'
                      name='name'
                      onChange={updateHandle}
                      placeholder='NAME'
                      value={contact.name}
                      className='form-control'
                    />
                  </div>
                  <div className='mb-2'>
                    <input
                      type='text'
                      name='photo'
                      onChange={updateHandle}
                      placeholder='Photo Url'
                      value={contact.photo}
                      className='form-control'
                    />
                  </div>
                  <div className='mb-2'>
                    <input
                      type='number'
                      name='mobile'
                      onChange={updateHandle}
                      placeholder='Mobile'
                      value={contact.mobile}
                      className='form-control'
                    />
                  </div>
                  <div className='mb-2'>
                    <input
                      type='email'
                      name='email'
                      onChange={updateHandle}
                      placeholder='Email'
                      value={contact.email}
                      className='form-control'
                    />
                  </div>
                  <div className='mb-2'>
                    <input
                      type='text'
                      name='company'
                      onChange={updateHandle}
                      placeholder='Company'
                      value={contact.company}
                      className='form-control'
                    />
                  </div>
                  <div className='mb-2'>
                    <input
                      type='text'
                      name='title'
                      onChange={updateHandle}
                      placeholder='Title'
                      value={contact.title}
                      className='form-control'
                    />
                  </div>
                  <div className='mb-2'>
                    <input
                      type='text'
                      name='groupId'
                      placeholder='Group'
                      value={contact.groupId}
                      onChange={updateHandle}
                      className='form-control'
                    />
                  </div>

                  <div>
                    <input type='submit' value='Update' className='btn btn-primary' />
                    <Link to={'/Contacts/list'} className='btn btn-danger ms-2'>Cancel</Link>
                  </div>
                </form>
              </div>
              <div className='col-md-6'>
                <img src={contact.photo} alt='' className='contact-img' />
              </div>
            </div>
          </div>
        </div>
      </section>
    </>
  );
};

export default EditContact;
