import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import Spinner from '../../../Assests/Spinner';
import { ContactServices } from '../../../Services/ContactServices';

const ContactList = () => {
  const [query, setQuery] = useState({
    text: "",
  });

  const [state, setState] = useState({
    loading: true,
    contacts: [],
    filteredContacts: [],
    errorMessage: '',
  });

  useEffect(() => {
    // Fetch contacts
    const fetchContacts = async () => {
      try {
        setState((prevState) => ({ ...prevState, loading: true }));
        const response = await ContactServices.getAllContacts();
        console.log("Fetched contacts:", response.data); // Log the fetched contacts

        // Check if response.data is an array
        if (Array.isArray(response.data)) {
          setState({
            loading: false,
            contacts: response.data,
            filteredContacts: response.data,
            errorMessage: '',
          });
        } else {
          console.error("Expected an array but got:", response.data);
          setState((prevState) => ({
            ...prevState,
            loading: false,
            errorMessage: 'Unexpected data format',
          }));
        }
      } catch (error) {
        console.error("Error fetching contacts:", error); // Log the error
        setState((prevState) => ({
          ...prevState,
          loading: false,
          errorMessage: 'Data not found',
        }));
      }
    };

    fetchContacts();
  }, []);

  // Delete contact
  const clickDelete = async (contactId) => {
    try {
      await ContactServices.deleteContact(contactId);
      console.log("Contact deleted, fetching updated contacts.");
      const response = await ContactServices.getAllContacts();
      console.log("Fetched updated contacts:", response.data); // Log the fetched updated contacts

      if (Array.isArray(response.data)) {
        setState({
          loading: false,
          contacts: response.data,
          filteredContacts: response.data,
          errorMessage: '',
        });
      } else {
        console.error("Expected an array but got:", response.data);
        setState((prevState) => ({
          ...prevState,
          loading: false,
          errorMessage: 'Unexpected data format',
        }));
      }
    } catch (error) {
      console.error("Error deleting contact:", error); // Log the error
      setState((prevState) => ({
        ...prevState,
        loading: false,
        errorMessage: 'Failed to delete contact',
      }));
    }
  };

  // Search contact
  const searchContact = (event) => {
    setQuery({ ...query, text: event.target.value });

    const theContacts = state.contacts.filter((contact) => {
      return contact.name.toLowerCase().includes(event.target.value.toLowerCase());
    });
    setState((prevState) => ({ ...prevState, filteredContacts: theContacts }));
  };

  const { loading, filteredContacts, errorMessage } = state;

  console.log("Filtered contacts:", filteredContacts); // Log before rendering

  return (
    <>
      <section className='contact-search'>
        <div className="container p-3">
          <div className="grid">
            <div className="row">
              <div className="col">
                <p className='h3'>
                  Contact Manager 
                  <Link to={'/Contacts/add'} className='btn btn-primary ms-2'>
                    <i className='fa fa-plus-circle me-2' />Add
                  </Link>
                </p>
                <p className='fst-italic'>
                  Lorem ipsum dolor sit amet consectetur adipisicing elit. Atque possimus accusantium illo nemo error. Harum vel aliquam rem. Neque harum modi quas consectetur et accusantium laborum, blanditiis nostrum in sapiente!
                </p>
              </div>
            </div>
          </div>
          <div className="row">
            <div className="col-md-6">
              <form action="" className='row'>
                <div className="col">
                  <div className="mb-2">
                    <input 
                      type="text" 
                      name='text' 
                      onChange={searchContact} 
                      value={query.text} 
                      placeholder='Search name' 
                      className=' form-control' 
                    />
                  </div>
                </div>
              </form>
            </div>
          </div>
        </div>
      </section>
      {
        loading ? <Spinner /> : <React.Fragment>
          <section className='contact-list'>
            <div className="container">
              <div className="row">
                {filteredContacts.length > 0 ? filteredContacts.map((contact) => {
                  let { photo, name, mobile, email, id } = contact;
                  return (
                    <div className="col-md-6 mb-4" key={id}>
                      <div className="card d-flex justify-content-around">
                        <div className="card-body">
                          <div className="row align-items-center">
                            <div className="col-md-4">
                              <img src={photo} alt="" className='contact-img' />
                            </div>
                            <div className="col-md-7">
                              <ul className="list-group">
                                <li className="list-group-item list-group-item-action">Name: {name}</li>
                                <li className="list-group-item list-group-item-action">Mobile: {mobile}</li>
                                <li className="list-group-item list-group-item-action">Email: {email}</li>
                              </ul>
                            </div>
                            <div className="col-md-1 d-flex flex-column align-items-center">
                              <Link to={`/Contacts/view/${contact.id}`} className='btn btn-warning my-1'>
                                <i className='fa fa-eye' />
                              </Link>
                              <Link to={`/Contacts/edit/${contact.id}`} className='btn btn-primary my-1'>
                                <i className='fa fa-pen' />
                              </Link>
                              <button className='btn btn-danger my-1' onClick={() => { clickDelete(contact.id) }}>
                                <i className='fa fa-trash' />
                              </button>
                            </div>
                          </div>
                        </div>
                      </div>
                    </div>
                  );
                }) : <p>No contacts found</p>}
              </div>
            </div>
          </section>
        </React.Fragment>
      }
      {errorMessage && <div className="alert alert-danger">{errorMessage}</div>} {/* Show error message if any */}
    </>
  );
}

export default ContactList;
