import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import Spinner from '../../../Assests/Spinner'

import { ContactServices } from '../../../Services/ContactServices';

const ContactList = () => {
  const [query, setQuery] = useState({
    text: ''
  });

  const [state, setState] = useState({
    loading: true,
    contacts: [],
    filteredContacts: [],
    errorMessage: ''
  });

  useEffect(() => {
    const fetchContacts = async () => {
      try {
        const response = await ContactServices.getAllContacts();
        setState({
          ...state,
          loading: false,
          contacts: response.data,
          filteredContacts: response.data // Adjust this if necessary to match your API response structure
        });
      } catch (error) {
        setState({
          ...state,
          loading: false,
          errorMessage: 'Data not found'
        });
      }
    };

    fetchContacts();
  }, []); // Only runs once when the component mounts

  const clickDelete = async (contactId) => {
    try {
      await ContactServices.deleteContact(contactId);
      // Refetch contacts after deletion
      const response = await ContactServices.getAllContacts();
      setState({
        ...state,
        contacts: response.data,
        filteredContacts: response.data // Update filtered contacts
      });
    } catch (error) {
      alert('Error deleting contact');
    }
  };

  const searchContact = (event) => {
    const { value } = event.target;
    setQuery({ text: value });

    const theContacts = state.contacts.filter(contact =>
      contact.name.toLowerCase().includes(value.toLowerCase())
    );
    setState(prevState => ({
      ...prevState,
      filteredContacts: theContacts
    }));
  };

  const { loading, filteredContacts, errorMessage } = state;

  return (
    <>
      <section className='contact-search'>
        <div className="container p-3">
          <div className="grid">
            <div className="row">
              <div className="col">
                <p className='h3'>
                  Contact Manager
                  <Link to={'/Contacts/add'} className='btn btn-primary ms-2'>
                    <i className='fa fa-plus-circle me-2' />Add
                  </Link>
                </p>
                <p className='fst-italic'>Lorem ipsum dolor sit amet consectetur adipisicing elit.</p>
              </div>
            </div>
          </div>
          <div className="row">
            <div className="col-md-6">
              <form className='row'>
                <div className="col">
                  <div className="mb-2">
                    <input
                      type="text"
                      name='text'
                      onChange={searchContact}
                      value={query.text}
                      placeholder='Search name'
                      className='form-control'
                    />
                  </div>
                </div>
              </form>
            </div>
          </div>
        </div>
      </section>
      {loading ? <Spinner /> : (
        <section className='contact-list'>
          <div className="container">
            <div className="row">
              {filteredContacts.length > 0 ? (
                filteredContacts.map(contact => {
                  const { photo, name, mobile, email, id } = contact;
                  return (
                    <div className="col-md-6 mb-4" key={id}>
                      <div className="card d-flex justify-content-around">
                        <div className="card-body">
                          <div className="row align-items-center">
                            <div className="col-md-4">
                              <img src={photo} alt={name} className='contact-img' />
                            </div>
                            <div className="col-md-7">
                              <ul className="list-group">
                                <li className="list-group-item list-group-item-action">Name: {name}</li>
                                <li className="list-group-item list-group-item-action">Mobile: {mobile}</li>
                                <li className="list-group-item list-group-item-action">Email: {email}</li>
                              </ul>
                            </div>
                            <div className="col-md-1 d-flex flex-column align-items-center">
                              <Link to={`/Contacts/view/${id}`} className='btn btn-warning my-1'>
                                <i className='fa fa-eye' />
                              </Link>
                              <Link to={`/Contacts/edit/${id}`} className='btn btn-primary my-1'>
                                <i className='fa fa-pen' />
                              </Link>
                              <button className='btn btn-danger my-1' onClick={() => clickDelete(id)}>
                                <i className='fa fa-trash' />
                              </button>
                            </div>
                          </div>
                        </div>
                      </div>
                    </div>
                  );
                })
              ) : (
                <p>No contacts found.</p>
              )}
            </div>
          </div>
        </section>
      )}
      {errorMessage && <div className="alert alert-danger">{errorMessage}</div>}
    </>
  );
};

export default ContactList;
