import React, { useEffect, useState } from 'react';
import { Link, useNavigate, useParams } from 'react-router-dom';
import { ContactServices } from '../../../Services/ContactServices';

const EditContact = () => {
  const navigate = useNavigate();
  const { contactId } = useParams(); // contactId from URL params
  const [loading, setLoading] = useState(true);
  const [contact, setContact] = useState({
    name: "",
    photo: "",
    mobile: "",
    email: "",
    title: "",
    company: "",
    groupId: ""
  });
  const [errorMessage, setErrorMessage] = useState("");

  useEffect(() => {
    const fetchContact = async () => {
      try {
        const response = await ContactServices.getContact(contactId);
        setContact(response.data);
      } catch (error) {
        setErrorMessage("Data not found!");
      } finally {
        setLoading(false);
      }
    };

    fetchContact();
  }, [contactId]);

  const updateHandle = (event) => {
    const { name, value } = event.target;
    setContact(prevContact => ({
      ...prevContact,
      [name]: value
    }));
  };

  const submitHandle = async (event) => {
    event.preventDefault();
    try {
      setLoading(true);
      await ContactServices.updateContact(contactId, contact);
      navigate("/contacts/list", { replace: true });
    } catch (error) {
      setErrorMessage("Error updating contact!");
    } finally {
      setLoading(false);
    }
  };

  if (loading) return <p>Loading...</p>;

  return (
    <section className='edit-contact'>
      <div className="container p-3">
        <div className="row">
          <div className="col">
            <p className="h3 text-primary">Edit Contact</p>
            {errorMessage && <p className="text-danger">{errorMessage}</p>}
            <form onSubmit={submitHandle}>
              <div className="mb-2">
                <input
                  type="text"
                  name="name"
                  value={contact.name}
                  onChange={updateHandle}
                  placeholder='NAME'
                  className='form-control'
                />
              </div>
              <div className="mb-2">
                <input
                  type="text"
                  name="photo"
                  value={contact.photo}
                  onChange={updateHandle}
                  placeholder='Photo URL'
                  className='form-control'
                />
              </div>
              <div className="mb-2">
                <input
                  type="number"
                  name="mobile"
                  value={contact.mobile}
                  onChange={updateHandle}
                  placeholder='Mobile'
                  className='form-control'
                />
              </div>
              <div className="mb-2">
                <input
                  type="email"
                  name="email"
                  value={contact.email}
                  onChange={updateHandle}
                  placeholder='Email'
                  className='form-control'
                />
              </div>
              <div className="mb-2">
                <input
                  type="text"
                  name="company"
                  value={contact.company}
                  onChange={updateHandle}
                  placeholder='Company'
                  className='form-control'
                />
              </div>
              <div className="mb-2">
                <input
                  type="text"
                  name="title"
                  value={contact.title}
                  onChange={updateHandle}
                  placeholder='Title'
                  className='form-control'
                />
              </div>
              <div className="mb-2">
                <input
                  type="text"
                  name="groupId"
                  value={contact.groupId}
                  onChange={updateHandle}
                  placeholder='Group'
                  className='form-control'
                />
              </div>

              <div>
                <input type="submit" value="Update" className="btn btn-primary" />
                <Link to={'/contacts/list'} className='btn btn-danger ms-2'>Cancel</Link>
              </div>
            </form>
          </div>
          <div className="col-md-6">
            <img src="https://img.freepik.com/free-vector/businessman-character-avatar-isolated_24877-60111.jpg?w=740&t=st=1726230511~exp=1726231111~hmac=61db9dba07cd25ba212fc3e18ea6ffc9b9cf4a2912cb76e75e0e8c9b92d6e76e" alt="" className='contact-img' />
          </div>
        </div>
      </div>
    </section>
  );
};

export default EditContact;
