import React, { useState, useEffect } from 'react';
import { Link, useNavigate, useParams } from 'react-router-dom';
import { ContactServices } from '../../../Services/ContactServices';

const EditContact = () => {
  let navigate = useNavigate();
  let { contactId } = useParams();

  let [state, setState] = useState({
    loading: true,
    contact: {
      name: '',
      photo: '',
      mobile: '',
      email: '',
      title: '',
      company: '',
      groupId: '',
    },
    errorMessage: ''
  });

  useEffect(() => {
    let promise = new Promise((res, rej) => {
      setState((prevState) => ({ ...prevState, loading: true }));
      let response = ContactServices.getContact(contactId);
      res(response);
    });

    promise
      .then((res1) => {
        if (res1.data && res1.data.data) {
          setState((prevState) => ({
            ...prevState,
            loading: false,
            contact: res1.data.data,
          }));
        }
      })
      .catch(() => {
        setState((prevState) => ({
          ...prevState,
          loading: false,
          errorMessage: 'Data not found',
        }));
      });
  }, [contactId]);

  let { loading, contact, errorMessage } = state;

  const updateHandle = (event) => {
    setState({
      ...state,
      contact: {
        ...state.contact,
        [event.target.name]: event.target.value,
      },
    });
  };

  const submitHandle = (event) => {
    event.preventDefault();
    let promise = new Promise((res, rej) => {
      setState((prevState) => ({ ...prevState, loading: true }));
      let postData = ContactServices.updateContact(contact, contactId);
      res(postData);
    });

    promise
      .then((res1) => {
        if (res1) {
          setState((prevState) => ({ ...prevState, loading: false }));
          navigate("/Contacts/list", { replace: true });
        } else {
          setState((prevState) => ({ ...prevState, loading: false }));
          navigate("/Contacts/edit", { replace: false });
        }
      })
      .catch(() => {
        setState((prevState) => ({
          ...prevState,
          loading: false,
          errorMessage: 'Data is not posted',
        }));
      });
  };

  // Add a loading state check
  if (loading) {
    return <p>Loading...</p>;
  }

  return (
    <>
      <pre>{JSON.stringify(contact)}</pre>
      <section className="edit-contact">
        <div className="container p-3">
          <div className="row">
            <div className="col">
              <p className="h3 text-primary">Edit Contact</p>
              <p className="fst-italic">
                Lorem ipsum dolor sit amet consectetur adipisicing elit.
              </p>
            </div>
            <div className="row align-items-center">
              <div className="col-md-4">
                <form onSubmit={submitHandle}>
                  <div className="mb-2">
                    <input
                      type="text"
                      name="name"
                      onChange={updateHandle}
                      placeholder="NAME"
                      value={contact.name || ''}
                      className="form-control"
                    />
                  </div>
                  <div className="mb-2">
                    <input
                      type="text"
                      name="photo"
                      onChange={updateHandle}
                      placeholder="Photo Url"
                      value={contact.photo || ''}
                      className="form-control"
                    />
                  </div>
                  <div className="mb-2">
                    <input
                      type="number"
                      name="mobile"
                      onChange={updateHandle}
                      placeholder="Mobile"
                      value={contact.mobile || ''}
                      className="form-control"
                    />
                  </div>
                  <div className="mb-2">
                    <input
                      type="email"
                      name="email"
                      onChange={updateHandle}
                      placeholder="Email"
                      value={contact.email || ''}
                      className="form-control"
                    />
                  </div>
                  <div className="mb-2">
                    <input
                      type="text"
                      name="company"
                      onChange={updateHandle}
                      placeholder="Company"
                      value={contact.company || ''}
                      className="form-control"
                    />
                  </div>
                  <div className="mb-2">
                    <input
                      type="text"
                      name="title"
                      onChange={updateHandle}
                      placeholder="Title"
                      value={contact.title || ''}
                      className="form-control"
                    />
                  </div>
                  <div className="mb-2">
                    <input
                      type="text"
                      name="groupId"
                      placeholder="Group"
                      value={contact.groupId || ''}
                      onChange={updateHandle}
                      className="form-control"
                    />
                  </div>

                  <div>
                    <input
                      type="submit"
                      value="Update"
                      className="btn btn-primary"
                    />
                    <Link to="/Contacts/list" className="btn btn-danger ms-2">
                      Cancel
                    </Link>
                  </div>
                </form>
              </div>
              <div className="col-md-6">
                <img src={contact.photo} alt="" className="contact-img" />
              </div>
            </div>
          </div>
        </div>
      </section>
    </>
  );
};

export default EditContact;
