import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import Spinner from '../../../Assests/Spinner';
import { ContactServices } from '../../../Services/ContactServices';

const ContactList = () => {
  const [query, setQuery] = useState({
    text: ''
  });

  const [state, setState] = useState({
    loading: true,
    contacts: [],
    filteredContacts: [],
    errorMessage: ''
  });

  useEffect(() => {
    setState({ ...state, loading: true });
    ContactServices.getAllContacts()
      .then((res1) => {
        // Adjusted to access the 'data' field inside the response
        const contactsData = res1.data.data || []; // Use the 'data' field from the response
        setState({
          ...state,
          loading: false,
          contacts: contactsData,
          filteredContacts: contactsData
        });
      })
      .catch((error) => {
        console.error('Error fetching contacts:', error);
        setState({
          ...state,
          loading: false,
          errorMessage: 'Data not found'
        });
      });
  }, []);

  // Delete contact
  const clickDelete = (contactId) => {
    ContactServices.deleteContact(contactId)
      .then((res1) => {
        if (res1) {
          // Re-fetch contacts after deletion
          setState({ ...state, loading: true });
          ContactServices.getAllContacts()
            .then((res2) => {
              const contactsData = res2.data.data || [];
              setState({
                ...state,
                loading: false,
                contacts: contactsData,
                filteredContacts: contactsData
              });
            })
            .catch((error) => {
              console.error('Error fetching contacts:', error);
              setState({
                ...state,
                loading: false,
                errorMessage: 'Data not found'
              });
            });
        }
      })
      .catch((error) => {
        console.error('Error deleting contact:', error);
      });
  };

  // Search contact
  const searchContact = (event) => {
    const searchText = event.target.value;
    setQuery({ ...query, text: searchText });

    if (Array.isArray(state.contacts)) {
      const theContacts = state.contacts.filter((contact) => {
        return contact.name
          .toLowerCase()
          .includes(searchText.toLowerCase());
      });
      setState({ ...state, filteredContacts: theContacts });
    }
  };

  const { loading, filteredContacts, errorMessage } = state;

  return (
    <>
      <section className='contact-search'>
        <div className='container p-3'>
          <div className='grid'>
            <div className='row'>
              <div className='col'>
                <p className='h3'>
                  Contact Manager{' '}
                  <Link to={'/Contacts/add'} className='btn btn-primary ms-2'>
                    <i className='fa fa-plus-circle me-2' />
                    Add
                  </Link>
                </p>
                <p className='fst-italic'>
                  Manage your contacts efficiently with our Contact Manager.
                </p>
              </div>
            </div>
          </div>
          <div className='row'>
            <div className='col-md-6'>
              <form className='row'>
                <div className='col'>
                  <div className='mb-2'>
                    <input
                      type='text'
                      name='text'
                      onChange={searchContact}
                      value={query.text}
                      placeholder='Search name'
                      className='form-control'
                    />
                  </div>
                </div>
                {/* Uncomment if you want a search button */}
                {/* <div className="col">
                  <div className="mb-2">
                    <input type="submit" value="Search" className='btn btn-outline-primary' />
                  </div>
                </div> */}
              </form>
            </div>
          </div>
        </div>
      </section>
      {loading ? (
        <Spinner />
      ) : (
        <React.Fragment>
          <section className='contact-list'>
            <div className='container'>
              <div className='row'>
                {filteredContacts.length > 0 ? (
                  filteredContacts.map((contact) => {
                    const { photo, name, mobile, email, id } = contact;
                    return (
                      <div className='col-md-6 mb-4' key={id}>
                        <div className='card d-flex justify-content-around'>
                          <div className='card-body'>
                            <div className='row align-items-center'>
                              <div className='col-md-4'>
                                <img
                                  src={photo || 'default-image-url.jpg'}
                                  alt=''
                                  className='contact-img'
                                />
                              </div>
                              <div className='col-md-7'>
                                <ul className='list-group'>
                                  <li className='list-group-item list-group-item-action'>
                                    Name: {name || 'N/A'}
                                  </li>
                                  <li className='list-group-item list-group-item-action'>
                                    Mobile: {mobile || 'N/A'}
                                  </li>
                                  <li className='list-group-item list-group-item-action'>
                                    Email: {email || 'N/A'}
                                  </li>
                                </ul>
                              </div>
                              <div className='col-md-1 d-flex flex-column align-items-center'>
                                <Link
                                  to={`/Contacts/view/${id}`}
                                  className='btn btn-warning my-1'
                                >
                                  <i className='fa fa-eye' />
                                </Link>
                                <Link
                                  to={`/Contacts/edit/${id}`}
                                  className='btn btn-primary my-1'
                                >
                                  <i className='fa fa-pen' />
                                </Link>
                                <button
                                  className='btn btn-danger my-1'
                                  onClick={() => {
                                    clickDelete(id);
                                  }}
                                >
                                  <i className='fa fa-trash' />
                                </button>
                              </div>
                            </div>
                          </div>
                        </div>
                      </div>
                    );
                  })
                ) : (
                  <p>No contacts available.</p>
                )}
              </div>
            </div>
          </section>
        </React.Fragment>
      )}
    </>
  );
};

export default ContactList;
